import React from "react";
import hoodie from '../../assets/images/mens/men-hoodie.jpg';
import bracelet from '../../assets/images/mens/men-bracelet.jpg';
import watch from '../../assets/images/womens/women-watch.jpg';
import tank from '../../assets/images/womens/women-black-tank.jpg';


function Carousel() {
  const myProductInfo = [hoodie, bracelet, watch, tank];

  const myItems = () => myProductInfo[Math.floor(Math.random() * myProductInfo.length)];

  const getImage = setInterval(myItems, 2000);


  return (
    <div id="carousel" data-testid="carousel">
      <div>
        <img src={myProductInfo[Math.floor(Math.random() * myProductInfo.length)]} alt={`${getImage}`}></img>
      </div>
      <button>•</button>
      <button>•</button>
    </div>
  )
}

setInterval(Carousel(), 1000)

export default Carousel;
